import React from "react";
import data from "../data/data";

export const Hero = () => {
  const { results } = data;
  const a = results[0].backdrop_path;
  return (
    <section className="h-96 overflow-hidden relative">
      <div className="absolute text-white font-bold">
        <h1 className="text-5xl">Welcome to MovieDB</h1>
        <h4>Check out all movies!</h4>
      </div>
      <img src={`https://image.tmdb.org/t/p/original${a}`} alt="img" />
    </section>
  );
};

export default Hero;
