import React from "react";
import data from "../data/data";

// Search for a movie, tv or actor...

export const Hero = () => {
  const { results } = data;
  const a = results[0].backdrop_path;
  return (
    <section className="">
      <div className="absolute z-10 w-full h-full flex flex-col justify-center">
        {/* Intro */}
        <div className="text-white font-bold">
          <h1 className="">Welcome!</h1>
          <h4 className="">Explore millions of movies!</h4>
        </div>

        {/* Search */}
        <form className="">
          <div className="">
            <input
              type="text"
              className=""
              placeholder="Search for a movie, tv or actor..."
            ></input>
            <button className="">Search</button>
          </div>
        </form>
      </div>

      <div className="bg-green-500 w-full h-full">
        <img
          className="opacity-80 object-cover w-full h-full lg:object-fit"
          src={`https://image.tmdb.org/t/p/original${a}`}
          alt="img"
        />
      </div>
    </section>
  );
};

export default Hero;
