import React from "react";
import data from "../data/data";

export const Hero = () => {
  const { results } = data;
  const a = results[0].backdrop_path;
  return (
    <section className="flex justify-center h-96 overflow-hidden relative">
      <div className="absolute text-white font-bold self-center subpixel-antialiased text-center">
        <div className="py-12">
          <h1 className="text-5xl ">Welcome to MovieDB!</h1>
          <h4 className="text-3xl ">Check out all movies!</h4>
        </div>

        <form>
          <div className="flex w-full bg-white rounded-lg">
            <input
              type="text"
              className="flex-1 p-3 pl-5 text-xl text-black outline-none rounded-lg"
              placeholder="Search for a movie, tv or actor..."
            ></input>
            <button className="btn">Search</button>
          </div>
        </form>
      </div>
      <div className="bg-green-500">
        <img
          className="object-cover opacity-90"
          src={`https://image.tmdb.org/t/p/original${a}`}
          alt="img"
        />
      </div>
    </section>
  );
};

export default Hero;
