import React from "react";

export const Card_Movie = ({
  title,
  release_date,
  vote_average,
  poster_path,
}) => {
  return (
    <div className="w-48 mx-5 inline-block">
      <div className="rounded-lg cursor-pointer shadow-lg overflow-hidden">
        <img
          src={`https://image.tmdb.org/t/p/w500${poster_path}`}
          alt="poster"
        />
        <div
          className={`h-6 ${
            vote_average >= 7
              ? `bg-green-400`
              : vote_average >= 4
              ? `bg-yellow-400`
              : `bg-red-400`
          }`}
        >
          <p className="text-center text-white font-bold ">
            {vote_average * 10}%
          </p>
        </div>
      </div>
      <div className="p-2">
        <h3 className="font-bold overflow-ellipsis">{title}</h3>
        <p className="text-gray-500">{release_date}</p>
      </div>
    </div>
  );
};

export default Card_Movie;
