import React from "react";
import movie_data from "../../data/movie_data";

const {
  backdrop_path,
  poster_path,
  title,
  tagline,
  release_date,
  runtime,
  genres,
  vote_average,
  overview,
  production_companies,
} = movie_data;
const heroImg = `https://image.tmdb.org/t/p/original${backdrop_path}`;
const poster = `https://image.tmdb.org/t/p/original${poster_path}`;

export const Hero = () => {
  return (
    <section
      style={{
        backgroundImage: `linear-gradient(rgba(0, 0, 0, 0.8), rgba(0, 0, 0, 0.8)),url("${heroImg}")`,
      }}
      className="content-center bg-fixed bg-cover bg-center mb-10 h-full flex flex-col justify-center items-center"
    >
      {/* Poster */}
      <div className="w-48">
        <img src={poster} alt="movie_poster" className="max-w-full" />
      </div>

      {/* Hero Info */}
      <div className="text-white">
        <h1 className="text-xl text-center">{title}</h1>
        {/* Sub Info */}
        <div className="text-gray-400 text-center">
          <p>{tagline}</p>
          <p>{release_date}</p>
          <p>{runtime}</p>
          <div className="flex justify-center">
            {genres.map((genre) => {
              const { id, name } = genre;
              return (
                <p key={id} className="mx-1">
                  {name}
                </p>
              );
            })}
          </div>
        </div>
        <div>
          <p>Overview</p>
          <p className="text-gray-400 truncate ">{overview}</p>
        </div>
        <div>
          <p>Produced by</p>
          <p></p>
        </div>
      </div>
    </section>
  );
};

export default Hero;
