import React from "react";
import { useMovieContext } from "../context/movie_context";
import { Horizontal_Scroller } from "./Horizontal_Scroller";

export const Recommendation = () => {
  const { fetchMovie } = useMovieContext();

  return (
    <div>
      {categoryList.map((item, index) => {
        const results = fetchMovie(
          `https://api.themoviedb.org/3/movie/${item.keyword}?api_key=d60f4e8797f13dd4c61d8414708bb669&language=en-US&page=1`
        );
        console.log("rec: " + results);
        return <Horizontal_Scroller />;
      })}
    </div>
  );
};

export default Recommendation;
