import React from "react";
import movie_data from "../../data/movie_data";

const {
  backdrop_path,
  poster_path,
  title,
  tagline,
  release_date,
  runtime,
  genres,
  vote_average,
  overview,
  production_companies,
} = movie_data;
const heroImg = `https://image.tmdb.org/t/p/original${backdrop_path}`;
const poster = `https://image.tmdb.org/t/p/original${poster_path}`;

export const Hero = () => {
  return (
    <section
      style={{
        backgroundImage: `linear-gradient(rgba(0, 0, 0, 0.8), rgba(0, 0, 0, 0.8)),url("${heroImg}")`,
      }}
      className="content-center bg-fixed bg-cover bg-center mb-10 h-screen flex flex-col justify-center items-center"
    >
      {/* Poster */}
      <div className="w-48">
        <img src={poster} alt="movie_poster" className="max-w-full" />
      </div>

      {/* Hero Info */}
      <div></div>
    </section>
  );
};

export default Hero;
