import React from "react";

// TODO:
// 1. FLITER COMPONENT
// 2. DIVIDER + SORT
// 3. GRIDVIEW + LISTVIEW
// REFERENCE: https://developers.themoviedb.org/3/discover/movie-discover
const genres = [
  "Action",
  "Adventure",
  "Animation",
  "Comedy",
  "Crime",
  "Documentary",
  "Drama",
  "Family",
];

export const Movies_Page = () => {
  return (
    <section className="border rounded-lg shadow-xl my-10 p-5 w-full self-center sm:w-3/4 md:w-5/6 lg:w-3/4">
      <form>
        <div className="lg:flex">
          <label for="input_date">Release Dates</label>
          <div id="input_date" className="sm:flex">
            <div className="flex justify-between sm:justify-start">
              <label
                for="release_date.gte"
                className="text-gray-300 sm:pr-10 lg:px-10"
              >
                from
              </label>
              <input
                type="date"
                id="release_date.gte"
                name="release_date.gte"
              />
            </div>
            <div className="flex justify-between sm:justify-start">
              <label for="release_date.lte" className="text-gray-300 sm:px-10">
                to
              </label>
              <input
                type="date"
                id="release_date.lte"
                name="release_date.lte"
              />
            </div>
          </div>
        </div>
        <div>
          <label for="">Genres</label>
          <ul>
            {genres.map((genre) => {
              return <li>{genre}</li>;
            })}
          </ul>
        </div>
        <input type="radio" />
      </form>
    </section>
  );
};

export default Movies_Page;
