import { useState } from "react";
import { useAppContext } from "../context/app_context";
import { links } from "./utils";

const hidden = "hidden";
const sideBar =
  "flex flex-col h-screen fixed top-0 right-0 z-50 bg-gray-100 w-2/5 p-4 lg:w-96";
const header = "p-4 border-b-2 border-gray-300";
const nav = "p-4";
const li = "py-2 px-1";
const a = "hover:underline hover:bg-gray-200 w-48 cursor-pointer block";

const Sidebar = () => {
  const { isSidebarOpen, closeSidebar } = useAppContext();
  return (
    <aside className={isSidebarOpen ? `${sideBar}` : `${hidden}`}>
      <div className={header}>
        <h2 className="text-green-300 text-4xl text-center font-semibold ">
          <a href="/#">MovieDB</a>
        </h2>
      </div>
      <nav className={nav}>
        <ul className="">
          {links.map((link, index) => {
            const { text, url } = link;
            return (
              <li id={index} className={li}>
                <a href={url} className={a}>
                  {text}
                </a>
              </li>
            );
          })}
        </ul>
      </nav>
    </aside>
  );
};

export default Sidebar;
