import React from "react";
import movie_data from "../../data/movie_data";

const {
  backdrop_path,
  poster_path,
  title,
  tagline,
  release_date,
  runtime,
  genres,
  vote_average,
  overview,
  production_companies,
} = movie_data;
const heroImg = `https://image.tmdb.org/t/p/original${backdrop_path}`;
const poster = `https://image.tmdb.org/t/p/original${poster_path}`;

export const Hero = () => {
  return (
    <section
      style={{
        backgroundImage: `linear-gradient(rgba(0, 0, 0, 0.8), rgba(0, 0, 0, 0.8)),url("${heroImg}")`,
      }}
      className="bg-fixed bg-cover bg-center mb-10 h-full"
    >
      {/* Content */}
      <div className="content-center flex flex-col justify-center items-center gap-y-2 py-5">
        {/* Poster */}
        <div className="w-48">
          <img src={poster} alt="movie_poster" className="max-w-full" />
        </div>

        {/* Hero Info */}
        <div className="text-white flex flex-col gap-y-2">
          {/* Title */}
          <div>
            <h1 className="text-2xl text-center">{title}</h1>
          </div>
          {/* Sub Info */}
          <div className="text-gray-400 text-center flex flex-col gap-y-2">
            <p>{tagline}</p>
            <p>{release_date}</p>
            <p>{runtime} mins</p>
            <div className="flex justify-center">
              {genres.map((genre) => {
                const { id, name } = genre;
                return (
                  <p key={id} className="mx-1 px-2 border rounded-xl">
                    {name}
                  </p>
                );
              })}
            </div>
          </div>

          {/* User Score */}
          <div className="flex">
            <p className="whitespace-nowrap mr-1">User Score</p>
            <div className="h-6 w-full bg-green-500 rounded-lg text-center font-bold">
              {vote_average * 10}%
            </div>
          </div>

          {/* Overview */}
          <div>
            <p>Overview</p>
            <p className="text-gray-400">{overview}</p>
          </div>

          {/* Company */}
          <div className="flex">
            <div>
              <p className="mr-1">Produced by </p>
            </div>

            <div className="inline-block">
              {production_companies.map((company) => {
                const { id, name } = company;
                return (
                  <p key={id} className="border rounded-xl">
                    {name}
                  </p>
                );
              })}
            </div>
          </div>
        </div>
      </div>
    </section>
  );
};

export default Hero;
