import React from "react";
import data from "../data/data";

// Search for a movie, tv or actor...

export const Hero = () => {
  const { results } = data;
  const heroImg = `https://image.tmdb.org/t/p/original${results[0].backdrop_path}`;
  return (
    <section
      className="bg-fixed bg-contain"
      style={{
        backgroundImage: `url("${heroImg}")`,
      }}
    >
      <div className="p-16">
        {/* Intro */}
        <div className="text-white font-bold py-4">
          <h1 className="text-5xl">Welcome!</h1>
          <h4 className="text-2xl">Explore millions of movies!</h4>
        </div>

        {/* Search */}
        <form className="p-">
          <div className="flex bg-white rounded-lg">
            <input
              type="text"
              className="flex-1 p-2 rounded-l-lg focus:outline-none"
              placeholder="Search for a movie, tv or actor..."
            ></input>
            <button className="btn ">Search</button>
          </div>
        </form>
      </div>

      {/* <div className="">
        <img
          className=""
          src={`https://image.tmdb.org/t/p/original${a}`}
          alt="img"
        />
      </div> */}
    </section>
  );
};

export default Hero;
