import { React, useState } from "react";
import { links, btn, btnGreen } from "./utils";

export const Navbar = () => {
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  return (
    <header className="flex py-4">
      <div className="flex-1">
        <h2 className="text-green-300 text-5xl text-center">
          <a href="/#">MovieD</a>B
        </h2>
      </div>
      <nav className="flex-1">
        <ul className="flex justify-center h-full items-center">
          {links.map((link, index) => {
            const { text, url } = link;
            return (
              <li id={index} className="hover:underline text-lg">
                <a href={url} className="px-4">
                  {text}
                </a>
              </li>
            );
          })}
        </ul>
      </nav>
      <div className="flex-1 flex justify-center items-center">
        <button className={`${btn} ${btnGreen}`}>Login</button>
        {isSidebarOpen && (
          <button className="p-4">
            <svg
              xmlns="http://www.w3.org/2000/svg"
              class="h-6 w-6"
              fill="none"
              viewBox="0 0 24 24"
              stroke="currentColor"
            >
              <path
                stroke-linecap="round"
                stroke-linejoin="round"
                stroke-width="2"
                d="M4 6h16M4 12h16M4 18h16"
              />
            </svg>
          </button>
        )}
      </div>
    </header>
  );
};

export default Navbar;
