import React from "react";
import { useMovieContext } from "../context/movie_context";
import { Horizontal_Scroller } from "./Horizontal_Scroller";

export const Recommendation = () => {
  const { movieCategories } = useMovieContext();
  //console.log("movieCategories: " + movieCategories);
  return (
    <section>
      {movieCategories.map((item, index) => {
        const { category, movies } = item;
        //console.log("category: " + category);
        console.log("RECmovies: " + movies);
        return (
          <Horizontal_Scroller
            key={index}
            category={category}
            movies={movies}
          />
        );
      })}
    </section>
  );
};

export default Recommendation;
