import React from "react";

export const Card_Person = ({ img, name, known_for }) => {
  return (
    <div className="shadow-lg rounded-lg overflow-hidden">
      <div>
        <img
          src={`https://image.tmdb.org/t/p/w500${img}`}
          alt="actor_pic"
          className="w-48"
        />
      </div>
      <div className="p-2">
        <h3 className="text-center text-lg font-semibold w-auto">{name}</h3>
        <p className="text-center text-gray-500 w-auto">{known_for[0].title}</p>
      </div>
    </div>
  );
};

export default Card_Person;
