import React from "react";
import Card_Movie from "./Card_Movie";

const c = "w-full overflow-x-auto whitespace-nowrap";

export const Horizontal_Scroller = ({ category, movies }) => {
  return (
    <section className="w-screen mb-10 md:w-full">
      <h1 className="text-center mb-10 text-4xl font-bold">{category}</h1>
      <div className="p-5 whitespace-nowrap overflow-x-scroll md:px-0">
        {movies.map((movie) => {
          const { id, title, release_date, vote_average, poster_path } = movie;
          return (
            <Card_Movie
              key={id}
              title={title}
              release_date={release_date}
              vote_average={vote_average}
              poster_path={poster_path}
            />
          );
        })}
      </div>
    </section>
  );
};

export default Horizontal_Scroller;
