import React, { useContext, useReducer, useEffect } from "react";
import axios from "axios";
import reducer from "../reducer/movie_reducer";
import {
  FETCH_TRENDING_MOVIE,
  FETCH_POPULAR_MOVIE,
  FETCH_TOP_RATED_MOVIE,
  FETCH_UPCOMING_MOVIE,
} from "../constants/action";

const MovieContext = React.createContext();
const api_url = "https://api.themoviedb.org/3";

const initialState = {
  api_key: "api_key=d60f4e8797f13dd4c61d8414708bb669&language=en-US&page=1",
  trending_movie: [],
  popular_movie: [],
  top_rated_movie: [],
  upcoming_movie: [],
};

const recommendation_list = [
  {
    category: "Trending",
    keyword: "trending/movie/day",
  },
  {
    category: "Popular",
    keyword: "movie/popular",
  },
  {
    category: "Top Rated",
    keyword: "movie/top_rated",
  },
  {
    category: "Upcoming",
    keyword: "movie/upcoming",
  },
];

export const MovieProvider = ({ children }) => {
  const [state, dispatch] = useReducer(reducer, initialState);

  const fetchMovie = async (action, url) => {
    // dispatch({ type: FETCH_POPULAR_MOVIE });
    // try {
    //   const response = await axios.get(url);
    //   //   console.log("Check");
    //   console.log(url);
    //   console.log(response.data.results);
    //   return response.data.results;
    // } catch (error) {}
  };

  useEffect(() => {
    fetchMovie(FETCH_TRENDING_MOVIE, url);
    fetchMovie(FETCH_POPULAR_MOVIE, url);
    fetchMovie(FETCH_TOP_RATED_MOVIE, url);
    fetchMovie(FETCH_UPCOMING_MOVIE, url);
  }, []);

  return <MovieContext.Provider>{children}</MovieContext.Provider>;
};

export const useMovieContext = () => {
  return useContext(MovieContext);
};
