import React from "react";
import Card_Movie from "./Card_Movie";
import popular_movie_data from "../data/popular_movie_data";

const c = "w-full overflow-x-auto whitespace-nowrap";

export const Horizontal_Scroller = () => {
  const { results } = popular_movie_data;
  //console.log(results);
  return (
    <section className="w-screen overflow-hidden py-5">
      <h1 className="text-center mb-10 text-4xl font-bold">Popular</h1>
      <div className="whitespace-nowrap overflow-x-scroll">
        {results.map((result, index) => {
          const { title, release_date, vote_average, poster_path } = result;
          const movie = { title, release_date, vote_average, poster_path };
          // console.log(title);
          return <Card_Movie key={index} {...movie} />;
        })}
      </div>
    </section>
  );
};

export default Horizontal_Scroller;
