import React from "react";
import data from "../data/data";

export const Hero = () => {
  const { results } = data;
  const a = results[0].backdrop_path;
  return (
    <section className="flex justify-center h-96 overflow-hidden relative">
      <div className="absolute text-white font-bold self-center ">
        <h1 className="text-5xl">Welcome to MovieDB!</h1>
        <h4 className="text-3xl text-center">Check out all movies!</h4>
        <form>
          <input type="text"></input>
        </form>
      </div>
      <img
        className="object-cover w-full"
        src={`https://image.tmdb.org/t/p/original${a}`}
        alt="img"
      />
    </section>
  );
};

export default Hero;
