import React from "react";

export const Card_Person = ({ img, name, known_for }) => {
  return (
    <div className="m-3 shadow-lg rounded-lg overflow-hidden">
      <div>
        <img
          src={`https://image.tmdb.org/t/p/w500${img}`}
          alt="actor_pic"
          className=""
        />
      </div>
      <div>
        <h1>{name}</h1>
      </div>
      <div>{known_for[0].title}</div>
    </div>
  );
};

export default Card_Person;
