import React, { useContext, useReducer, useEffect } from "react";
import axios from "axios";
import reducer from "../reducer/movie_reducer";
import { FETCH_POPULAR_MOVIE } from "../constants/action";

const MovieContext = React.createContext();
const popular_movie_url =
  "https://api.themoviedb.org/3/movie/popular?api_key=d60f4e8797f13dd4c61d8414708bb669&language=en-US&page=1";

const initialState = {
  popular_movie: [],
};

export const MovieProvider = ({ children }) => {
  const [state, dispatch] = useReducer(reducer, initialState);

  const fetchMovie = async (url) => {
    console.log(url);
    dispatch({ type: FETCH_POPULAR_MOVIE });
    // try {
    //   const response = await axios.get(url);
    //   //   console.log("Check");
    //   //   console.log(response.data.results);
    //   return response.data.results;
    // } catch (error) {}
  };

  //   useEffect(() => {
  //     fetchPopularMovie(url);
  //   }, []);

  return (
    <MovieContext.Provider value={{ fetchMovie }}>
      {children}
    </MovieContext.Provider>
  );
};

export const useMovieContext = () => {
  return useContext(MovieContext);
};
