import React, { useContext, useReducer, useEffect } from "react";
import axios from "axios";
import reducer from "../reducer/movie_reducer";
import {
  FETCH_TRENDING_MOVIE,
  FETCH_POPULAR_MOVIE,
  FETCH_TOP_RATED_MOVIE,
  FETCH_UPCOMING_MOVIE,
  FETCH_MOVIE_GENRE,
} from "../constants/action";
import popular_movie_data from "../data/popular_movie_data";

const MovieContext = React.createContext();

const initialState = {
  api_url: "https://api.themoviedb.org/3",
  api_key: "d60f4e8797f13dd4c61d8414708bb669",
  movie_arr: [
    {
      category: "Trending",
      keyword: "/trending/movie/day",
      action: FETCH_TRENDING_MOVIE,
      movies: popular_movie_data.results,
    },
    {
      category: "Popular",
      keyword: "/movie/popular",
      action: FETCH_POPULAR_MOVIE,
      movies: popular_movie_data.results,
    },
    {
      category: "Top Rated",
      keyword: "/movie/top_rated",
      action: FETCH_TOP_RATED_MOVIE,
      movies: popular_movie_data.results,
    },
    {
      category: "Upcoming",
      keyword: "/movie/upcoming",
      action: FETCH_UPCOMING_MOVIE,
      movies: popular_movie_data.results,
    },
  ],
  // {id,name}
  genres: [],
};

export const MovieProvider = ({ children }) => {
  const [state, dispatch] = useReducer(reducer, initialState);

  const fetchMovie = async (action, url) => {
    try {
      // const response = await axios.get(url);
      // const movies = response.data.results;
      const movies = popular_movie_data.results;
      dispatch({ type: action, payload: movies });
    } catch (error) {}
  };

  const fetchGenre = async () => {
    try {
      const url = `https://api.themoviedb.org/3/genre/movie/list?api_key=${state.api_key}&language=en-US`;
      const response = await axios.get(url);
      console.log(response);
      const genres = response.data.genres;
      dispatch({ type: FETCH_MOVIE_GENRE, payload: genres });
    } catch (error) {}
  };

  // Fetch Movie of different categories
  useEffect(() => {
    const list = state.movie_arr;
    list.forEach((item) => {
      const action = item.action;
      const url = `${state.api_url}${item.keyword}?api_key=${state.api_key}&language=en-US&page=1`;
      fetchMovie(action, url);
    });
  }, []);

  return (
    <MovieContext.Provider value={{ ...state, fetchGenre }}>
      {children}
    </MovieContext.Provider>
  );
};

export const useMovieContext = () => {
  return useContext(MovieContext);
};
