import { useState } from "react";
import { useAppContext } from "../context/app_context";

const hidden = "hidden";
const sideBar =
  "flex flex-col h-screen fixed top-0 right-0 z-50 bg-gray-200 w-1/2 lg:w-96";
const header = "p-4";

const Sidebar = () => {
  const { isSidebarOpen, closeSidebar } = useAppContext();
  return (
    <aside className={isSidebarOpen ? `${sideBar}` : `${hidden}`}>
      <div className={header}>
        <h2 className="text-green-300 text-4xl text-start font-semibold ml-8 lg:text-center lg:m-0">
          <a href="/#">MovieDB</a>
        </h2>
      </div>
      <ul>
        <li>Home</li>
        <li>Home</li>
        <li>Home</li>
        <li>Home</li>
      </ul>
    </aside>
  );
};

export default Sidebar;
