import { React, useState, useEffect } from "react";
import movie_data from "../../data/movie_data";
import movie_crew from "../../data/movie_crew";

export const Detail = () => {
  const [detail, setDetail] = useState({});

  useEffect(() => {
    const { title, spoken_languages, budget, revenue } = movie_data;
    const { crew } = movie_crew;
    const director = crew.find((person) => person.job === "Director").name;
  }, []);

  return (
    <div>
      <h1 className="content-header">Detail</h1>
      {/* Title */}
      <div>
        <h1>Title</h1>
        <p>{title}</p>
      </div>
      {/* Director */}
      <div>
        <h1>Director</h1>
        <p>{title}</p>
      </div>
    </div>
  );
};

export default Detail;
