import { useState } from "react";
import { useAppContext } from "../context/app_context";
import { links } from "./utils";

const sideBarClose = "box-border transition duration-500 ease-in-out w-0";
const sideBar =
  "flex flex-col h-screen fixed top-0 right-0 z-50 bg-gray-100 w-2/5 p-4 lg:w-96";
const header = "p-4 border-b-2 border-gray-300 flex";
const h2 = "flex-1 text-green-300 text-4xl text-start font-semibold";
const btnClose = "flex-end p-1";
const nav = "p-4";
const li = "";
const a =
  "hover:underline hover:bg-gray-200 w-full cursor-pointer block p-2 py-2";

const Sidebar = () => {
  const { isSidebarOpen, closeSidebar } = useAppContext();
  return (
    <aside className={isSidebarOpen ? `${sideBar}` : `${sideBarClose}`}>
      <div className={header}>
        <h2 className={h2}>
          <a href="/#">MovieDB</a>
        </h2>
        <button className={btnClose} onClick={closeSidebar}>
          <svg
            xmlns="http://www.w3.org/2000/svg"
            class="h-6 w-6"
            fill="none"
            viewBox="0 0 24 24"
            stroke="currentColor"
          >
            <path
              stroke-linecap="round"
              stroke-linejoin="round"
              stroke-width="2"
              d="M6 18L18 6M6 6l12 12"
            />
          </svg>
        </button>
      </div>
      <nav className={nav}>
        <ul className="">
          {links.map((link, index) => {
            const { text, url } = link;
            return (
              <li id={index} className={li}>
                <a href={url} className={a}>
                  {text}
                </a>
              </li>
            );
          })}
        </ul>
      </nav>
    </aside>
  );
};

export default Sidebar;
