import {
  FETCH_TRENDING_MOVIE,
  FETCH_POPULAR_MOVIE,
  FETCH_TOP_RATED_MOVIE,
  FETCH_UPCOMING_MOVIE,
} from "../constants/action";

const movie_reducer = (state, action) => {
  const temp_movie_arr = state.movie_arr;
  temp_movie_arr.forEach((item) => {
    if (item.action === action) item.movies = action.payload;
  });
  return { ...state, movie_arr: temp_movie_arr };
};

export default movie_reducer;
