import { React, useState, useEffect } from "react";

const certifications = ["NR", "G", "PG", "PG-13", "R", "NC-17"];
export const Filter = ({ fetchGenre, genres }) => {
  const [keyword, setKeyword] = useState("");
  const [with_genres, setWith_genres] = useState([]);

  useEffect(() => fetchGenre(), []);

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("keyword: " + keyword);
  };

  const test = (e) => {
    const genre_id = e.target.value;
    if (with_genres.includes(genre_id)) {
      with_genres.push(genre_id);
    } else {
      const temp_arr = with_genres.filter((id) => id !== genre_id);
    }
  };

  return (
    <section className="border rounded-lg shadow-xl my-10 p-5 w-full self-center sm:w-3/4 md:w-5/6 lg:w-3/4">
      <form onSubmit={(e) => handleSubmit(e)}>
        {/* Release Date */}
        <div className="my-1 lg:flex">
          <label htmlFor="input_date">Release Dates</label>
          <div id="input_date" className="sm:flex">
            <div className="flex justify-between sm:justify-start">
              <label
                htmlFor="release_date.gte"
                className="text-gray-400 sm:pr-10 lg:px-10"
              >
                from
              </label>
              <input
                type="date"
                id="release_date.gte"
                name="release_date.gte"
              />
            </div>
            <div className="flex justify-between sm:justify-start">
              <label
                htmlFor="release_date.lte"
                className="text-gray-400 sm:px-10"
              >
                to
              </label>
              <input
                type="date"
                id="release_date.lte"
                name="release_date.lte"
              />
            </div>
          </div>
        </div>
        {/* Genres */}
        <div className="flex items-center my-1 ">
          <label className="mr-1">Genres</label>
          <ul className="flex flex-wrap">
            {genres.map((genre) => {
              const { id, name } = genre;
              return (
                <li key={id} className="py-1 pr-1 my-1 mr-1">
                  <label className="border border-gray-300 p-1 rounded-lg cursor-pointer">
                    <input
                      type="checkbox"
                      id={name}
                      name={name}
                      className="mr-1"
                      value={id}
                      onChange={(e) => {
                        test(e);
                      }}
                    />
                    {name}
                  </label>
                </li>
              );
            })}
          </ul>
        </div>
        {/* Certification */}
        <div className="flex items-center my-1 ">
          <label className="mr-1">Certification</label>
          <ul className="flex flex-wrap">
            {certifications.map((certification, index) => {
              return (
                <li key={index} className="py-1 pr-1 my-1 mr-1">
                  <label className="border border-gray-300 p-1 rounded-lg cursor-pointer">
                    <input
                      type="checkbox"
                      id={index}
                      name={certification}
                      className="mr-1"
                    />
                    {certification}
                  </label>
                </li>
              );
            })}
          </ul>
        </div>
        {/* User Score */}
        <div className="flex my-1 ">
          <label>User Score</label>
          <div className="ml-1">
            <input
              type="text"
              placeholder="0"
              className="border border-gray-400 rounded text-center w-8"
            />
            <span className="text-gray-400"> to </span>
            <input
              type="text"
              placeholder="10"
              className="border border-gray-400 rounded text-center w-8"
            />
          </div>
        </div>
        {/* Keywords */}
        <div className="my-2">
          <label>Keywords </label>
          <input
            type="text"
            className="border border-gray-400 rounded pl-1"
            value={keyword}
            onChange={(e) => setKeyword(e.target.value)}
          />
        </div>
        {/* Search */}
        <div>
          <button type="submit" className="btn w-full">
            Search
          </button>
        </div>
      </form>
    </section>
  );
};

export default Filter;
