import React from "react";
import movie_data from "../../data/movie_data";

const { backdrop_path } = movie_data;
const heroImg = `https://image.tmdb.org/t/p/original${backdrop_path}`;

export const Hero = () => {
  return (
    <section
      style={{
        backgroundImage: `linear-gradient(rgba(7, 77, 0, 0.5), rgba(7, 77, 0, 0.5)),url("${heroImg}")`,
      }}
      className="bg-fixed bg-cover mb-10"
    >
      Hero
    </section>
  );
};

export default Hero;
