import { React, useState } from "react";
import { links, btn, btnGreen } from "./utils";
import { useAppContext } from "../context/app_context";

const Navbar = () => {
  const { openSidebar } = useAppContext();
  return (
    <header className="flex py-4 border-b-2 border-gray-300">
      {/* LOGO */}
      <div className="flex-1">
        <h2 className="text-green-300 text-4xl text-start font-semibold ml-8 lg:text-center lg:m-0">
          <a href="/#">MovieDB</a>
        </h2>
      </div>
      {/* NAV */}
      <nav className="flex-1 hidden lg:block">
        <ul className="flex justify-center h-full items-center">
          {links.map((link, index) => {
            const { text, url } = link;
            return (
              <li id={index} className="hover:underline text-lg">
                <a href={url} className="px-4">
                  {text}
                </a>
              </li>
            );
          })}
        </ul>
      </nav>
      {/* BUTTONS */}
      <div className="flex-1 flex justify-end items-center lg:justify-center">
        <button
          className={`${btn} transition duration-500 ease-in-out ${btnGreen}`}
        >
          Login
        </button>
        <button className="p-1 ml-2 mr-8 lg:hidden">
          <svg
            xmlns="http://www.w3.org/2000/svg"
            class="h-8 w-8"
            fill="none"
            viewBox="0 0 24 24"
            stroke="currentColor"
          >
            <path
              stroke-linecap="round"
              stroke-linejoin="round"
              stroke-width="2"
              d="M4 6h16M4 12h16M4 18h16"
            />
          </svg>
        </button>
      </div>
    </header>
  );
};

export default Navbar;
