import React from "react";
import data from "../data/data";

export const Hero = () => {
  const { results } = data;
  const a = results[0].backdrop_path;
  return (
    <section className="flex h-96">
      <div className="flex relative overflow-hidden justify-center">
        <div className="absolute text-white font-bold self-center subpixel-antialiased text-center z-10">
          <div className="py-12">
            <h1 className="text-5xl sm:text-3x1">Welcome to MovieDB!</h1>
            <h4 className="text-3xl ">Check out all movies!</h4>
          </div>

          <form className="px-4 lg:px-0">
            <div className="flex w-full bg-white rounded-lg">
              <input
                type="text"
                className="flex-1 p-3 pl-5 text-xl text-black outline-none rounded-lg"
                placeholder="Search for a movie, tv or actor..."
              ></input>
              <button className="btn">Search</button>
            </div>
          </form>
        </div>
        <div className="bg-green-500 w-full h-full">
          <img
            className="opacity-80 object-cover w-full h-full lg:object-fit"
            src={`https://image.tmdb.org/t/p/original${a}`}
            alt="img"
          />
        </div>
      </div>
    </section>
  );
};

export default Hero;
