import React from "react";

// TODO:
// 1. FLITER COMPONENT
// 2. DIVIDER + SORT
// 3. GRIDVIEW + LISTVIEW
// REFERENCE: https://developers.themoviedb.org/3/discover/movie-discover
export const Movies_Page = () => {
  return (
    <section className="border rounded-lg shadow-xl my-10 p-5 leading-loose">
      <form>
        <div>
          <label for="release_date.gte" className="filter_label">
            Release Dates
          </label>
          <p className="text-gray-300">from</p>
          <input
            type="date"
            id="release_date.gte"
            name="release_date.gte"
            className="filter__input"
          />
        </div>
      </form>
    </section>
  );
};

export default Movies_Page;
