import React from "react";
import { useMovieContext } from "../context/movie_context";
import { Horizontal_Scroller } from "./Horizontal_Scroller";
import popular_movie_data from "../data/popular_movie_data";

export const Recommendation = () => {
  const { movie_arr } = useMovieContext();

  return (
    <section>
      {movie_arr.map((item, index) => {
        const { category, movies } = item;
        return <Horizontal_Scroller key={index} category movies />;
      })}
    </section>
  );
};

export default Recommendation;
